export const generateSnippet = (content: any[], maxLength: number = 200): string => {
    const plainText = content.filter(item => item.type === 'paragraph').map(item => item.text).join(' ');
    
    const cleanContent = plainText.replace(/\s+/g, ' ').trim();

    if (cleanContent.length <= maxLength) {
        return cleanContent;
    }

    return cleanContent.substring(0, maxLength) + '...';
};

export const getBlogPostById = (id: string) => {
    return blogPosts.find(post => post.id === id);
}

export const getAllBlogPosts = () => {
    return blogPosts.map(post => ({
        ...post,
        snippet: generateSnippet(post.content)
    }));
}